import React from "react";
import ReactDOM from "react-dom";
import Hello from "./Hello";
import BalticCountries from "./BalticCountries";
import Countries from "./Countries";
import "./styles.css";

import "./App.css";

function App() {
  let countries = [
    { name: "France", capital: "Paris" },

    {
      name: "USA",
      capital: "Washinton.D.C",
    },

    { name: "Australia", capital: "Canberra" },
  ];
  return (
    <div className="App">
      <h1>React Conditional Rendering</h1>
      <h3>Challenge 1</h3>
      <p>
        Create a new Component Hello return a h2 with the word "Hello" in it.
        Include the component here.
      </p>
      <Hello />
      {/* HelloHello */}
      <h3>Challenge 2</h3>
      <p>
        Send a property language and the component should render "Bonjour" if
        language is french and "Hello" in other cases. Include the component
        here for English and French
      </p>
      <Hello language="french" />
      <Hello language="english" />
      {/* BonjourHello HelloHello */}
      <h3>Challenge 3</h3>
      <p>
        Add the ability for the component to render "Hola" for Spanish. Include
        the component here for Spanish
      </p>
      <Hello language="spanish" />
      {/*       HelloHalo */}

      <strong>Week3 Less3</strong>
      <h1>React Loops</h1>

      <h3>Challenge 1</h3>
      <p>
        Create a new Component called BalticCountries and list the 3 baltic
        countries (Estonia, Latvia, Lithuania) without using loops or variables.
        Include the component below.
      </p>
      <BalticCountries />

      <h3>Challenge 2</h3>
      <p>
        Include a component called Countries below and send the variable
        countries (created above) as a property. Create a new component called
        Countries and list all countries from the Countries component using a
        loop.
      </p>
      <Countries countries={countries} />

      <h3>Challenge 3</h3>
      <p>
        Modify the countries variable in the code above so it includes the
        country name but also the capital city of each country. Display each
        country and capital city in the Countries component
      </p>
    </div>
  );
}

const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);
